package com.basic;

public class Checking_Even_Odd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number=150;
		if(number%2==0){
		System.out.println("EVEN");
		System.out.println("BYE");
		}
		else{
			System.out.println("ODD");
		System.out.println("BYE");
	}}

}
